import { recognize } from 'tesseract.js';
import * as i0 from "@angular/core";
import * as i1 from "src/app/servicios/userservice.service";
import * as i2 from "@angular/forms";
const _c0 = ["inputImage"];
const _c1 = ["outputImage"];
export class ExtraccionComponent {
    constructor(userservice) {
        this.userservice = userservice;
        this.bandera = false;
        this.cx = CanvasRenderingContext2D;
        this.fileToUpload = null;
        this.shortLink = "";
        this.loading = false; // Flag variable    
        this.datos_factura = {
            name_empresa: '',
            direccion: '',
            telefono: '',
            fecha_literal: '',
            nit: '',
            nombre_cliente: '',
            valor_total_literal: '',
            total_bruto: '',
            total_descuento: '',
            total_producto: '',
            fecha_numerica: '',
        };
        this.tabla_contenido = [];
        this.loadingProgress = (logger) => {
            console.log(logger);
        };
        this.initializacion = async () => {
            const carga = true;
            const imageElement = this.inputImage.nativeElement;
            const { data } = await recognize(imageElement, 'spa', {
                logger: m => this.loadingProgress(m)
            });
            console.log('laptamre', data.words);
            this.draw(data);
        };
        this.initSetup = () => {
            const imageElement = this.inputImage.nativeElement;
            const canvasElement = this.outputimage.nativeElement;
            const that = this;
            imageElement.onload = function () {
                const { naturalWidth, naturalHeight } = imageElement;
                console.log(naturalWidth, naturalHeight);
                that.cx = canvasElement.getContext('2d');
                that.cx.lineWidth = 5;
                that.cx.lineCap = 'square';
                that.cx.strokeStyle = 'green';
                canvasElement.width = naturalWidth;
                canvasElement.height = naturalHeight;
                console.log('initsetup');
            };
            this.initializacion();
        };
        this.draw = (dataIn) => {
            dataIn.words.forEach(w => {
                const bounding = w.bbox;
                console.log(bounding);
                this.cx.strokeStyle = 'green';
                //falta ver que pasa con strokestyle
                this.cx.strokeRect(bounding.x0, bounding.y0, bounding.x1 - bounding.x0, bounding.y1 - bounding.y0);
                this.cx.beginPath();
                this.cx.moveTo(w.baseline.x0, w.baseline.y0);
                this.cx.lineTo(w.baseline.x1, w.baseline.y1);
                this.cx.stroke();
            });
        };
        this.pdfcargado = () => {
            console.log('imagen lista');
        };
    }
    ngOnChanges(changes) {
        throw new Error('Method not implemented.');
    }
    ngOnInit() {
        // this.obtenerdatosJson();
    }
    onChange(event) {
        this.file = event.target.files[0];
        // this.pdfcargado();
        // this.initializacion();
        this.initSetup();
    }
    ngAfterViewInit() {
    }
    enviardata() {
        if (this.file) {
            this.loading = !this.loading;
            this.userservice.obtenerdatosjson(this.file).subscribe((resp) => {
                // console.log(resp.output[0].fields.date.valueData.text);
                this.datos_factura.name_empresa = resp.output[0].fields.name_empresa.valueData.text;
                console.log(resp.output[0].fields.name_empresa.valueData.text);
                this.datos_factura.direccion = resp.output[0].fields.direccion.valueData.text;
                this.datos_factura.telefono = resp.output[0].fields.telefono.valueData.text;
                this.datos_factura.fecha_literal = resp.output[0].fields.fecha_literal.valueData.text;
                this.datos_factura.nit = resp.output[0].fields.nit.valueData.text;
                this.datos_factura.nombre_cliente = resp.output[0].fields.nombre_cliente.valueData.text;
                this.datos_factura.valor_total_literal = resp.output[0].fields.valor_total_literal.valueData.text;
                this.datos_factura.total_bruto = resp.output[0].fields.total_bruto.valueData.text;
                this.datos_factura.total_descuento = resp.output[0].fields.total_descuento.valueData.text;
                // this.datos_factura.total_producto = resp.output[0].fields.total_producto.valueData.text
                this.datos_factura.fecha_numerica = resp.output[0].fields.fecha_numerica.valueData.text;
            });
        }
        else {
            alert("NO EXISTE UN ARCHIVO CARGADO!");
        }
    }
    enviardatatable() {
        if (this.file) {
            this.loading = !this.loading;
            this.userservice.obtenerdatosjson(this.file).subscribe((resp) => {
                // console.log(resp.output[0].fields.date.valueData.text);
                this.tabla_contenido = resp.output[0].fields.valores_tabla_contenido.value;
                console.log(this.tabla_contenido.length);
            });
        }
        else {
            alert("No existe archivo cargado para extraer contenido");
        }
    }
    handleFileInput(files) {
        this.fileToUpload = files.item(0);
    }
}
