export class ExtraccionCamaraComponent {
    constructor(userservice, router) {
        this.userservice = userservice;
        this.router = router;
        this.bandera = false;
        this.cx = CanvasRenderingContext2D;
        this.fileToUpload = null;
        this.shortLink = "";
        this.loading = false; // Flag variable    
        this.datos_factura = {
            name_empresa: '',
            direccion: '',
            telefono: '',
            fecha_literal: '',
            nit: '',
            nombre_cliente: '',
            valor_total_literal: '',
            total_bruto: '',
            total_descuento: '',
            total_producto: '',
            fecha_numerica: '',
