                this.cx.lineTo(w.baseline.x1, w.baseline.y1);
                this.cx.stroke();
            });
        };
        this.pdfcargado = () => {
            console.log('imagen lista');
        };
    }
    ngOnChanges(changes) {
        throw new Error('Method not implemented.');
    }
