                this.datos_factura.total_descuento = resp.output[0].fields.total_descuento.valueData.text;
                this.datos_factura.numero_item = resp.output[0].fields.numero_item.valueData.text;
                this.datos_factura.codigo_producto = resp.output[0].fields.codigo_producto.valueData.text;
                this.datos_factura.descripcion_producto = resp.output[0].fields.descripcion_producto.valueData.text;
                this.datos_factura.udm = resp.output[0].fields.udm.valueData.text;
                this.datos_factura.cantidad_producto = resp.output[0].fields.cantidad_producto.valueData.text;
                this.datos_factura.precio_unitario = resp.output[0].fields.precio_unitario.valueData.text;
                this.datos_factura.total_producto = resp.output[0].fields.total_producto.valueData.text;
                this.datos_factura.fecha_numerica = resp.output[0].fields.fecha_numerica.valueData.text;
                // resp.output[0].fields.fecha_numerica.valueData.value[i]['value']['valueData']['text']
            });
        }
        else {
            alert("NO EXISTE UN ARCHIVO CARGADO!");
        }
    }
    handleFileInput(files) {
        this.fileToUpload = files.item(0);
    }
}
