import { recognize } from 'tesseract.js';
import Swal from 'sweetalert2';
import * as i0 from "@angular/core";
import * as i1 from "src/app/servicios/userservice.service";
import * as i2 from "@angular/router";
import * as i3 from "@angular/material/toolbar";
import * as i4 from "@angular/material/icon";
import * as i5 from "@angular/material/grid-list";
const _c0 = ["inputImage"];
const _c1 = ["outputImage"];
export class ExtraccionComponent {
    constructor(userservice, router) {
        this.userservice = userservice;
        this.router = router;
        this.bandera = false;
        this.cx = CanvasRenderingContext2D;
        this.fileToUpload = null;
        this.shortLink = "";
        this.loading = false; // Flag variable    
        // addcabecera_datos
        // datos_cabecera={
        //   nombre_empresa: '',
        //   direccion_empresa: '',
        //   telefono_empresa: '',
        //   lugar_fecha: '',
        //   nit: '',
        //   cliente_archivo: '',
        //   tipo_archivo: '',
        //   valor_total_literal: '',
        //   total_archivo: ''
        // }
        this.valores_cargados = false;
        this.lineas_valores_cargados = false;
        this.datos_factura = {
            nombre_empresa: '',
            direccion_empresa: '',
            telefono_empresa: '',
            lugar_fecha: '',
            nit: '',
            cliente_archivo: '',
            tipo_archivo: '',
            valor_total_literal: '',
            total_archivo: ''
        };
        this.contenido_tabla = [];
        this.algoencamra = (event) => {
            this.filecam = event.target.src;
            console.log('porfin!', this.filecam);
        };
        this.loadingProgress = (logger) => {
            console.log(logger);
        };
        this.initializacion = async () => {
            const carga = true;
            const imageElement = this.inputImage.nativeElement;
            const { data } = await recognize(imageElement, 'spa', {
                logger: m => this.loadingProgress(m)
            });
            console.log('laptamre', data.words);
            this.draw(data);
        };
        this.initSetup = () => {
            const imageElement = this.inputImage.nativeElement;
            const canvasElement = this.outputimage.nativeElement;
            const that = this;
            imageElement.onload = function () {
                const { naturalWidth, naturalHeight } = imageElement;
                console.log(naturalWidth, naturalHeight);
                that.cx = canvasElement.getContext('2d');
                that.cx.lineWidth = 5;
                that.cx.lineCap = 'square';
                that.cx.strokeStyle = 'green';
                canvasElement.width = naturalWidth;
                canvasElement.height = naturalHeight;
                console.log('initsetup');
            };
            this.initializacion();
        };
        this.draw = (dataIn) => {
            dataIn.words.forEach(w => {
                const bounding = w.bbox;
                console.log(bounding);
                this.cx.strokeStyle = 'green';
                //falta ver que pasa con strokestyle
                this.cx.strokeRect(bounding.x0, bounding.y0, bounding.x1 - bounding.x0, bounding.y1 - bounding.y0);
                this.cx.beginPath();
                this.cx.moveTo(w.baseline.x0, w.baseline.y0);
                this.cx.lineTo(w.baseline.x1, w.baseline.y1);
                this.cx.stroke();
            });
        };
        this.pdfcargado = () => {
            console.log('imagen lista');
        };
        this.ir_atras = () => {
            this.router.navigate(['/tipo_extraccion']);
        };
    }
    ngOnChanges(changes) {
        throw new Error('Method not implemented.');
    }
    ngOnInit() {
        // this.obtenerdatosJson();
    }
    onChange(event) {
        this.file = event.target.files[0];
        this.initSetup();
    }
    onChanged(event) {
        // this.filecam = event.target.src;
        // console.log('inciiar', this.filecam)
        this.initSetup();
    }
    ngAfterViewInit() {
    }
    guardar_datos_cabecera() {
        if (this.valores_cargados == true) {
            this.userservice.addcabecera_datos(this.datos_factura).subscribe((resp) => {
                Swal.fire({
                    icon: 'success',
                    title: 'Datos guardados',
                    showConfirmButton: false,
                    timer: 3000,
                });
            });
        }
        else {
            Swal.fire({
                icon: 'error',
                title: 'No hay nada para guardar',
                showConfirmButton: false,
                timer: 3000,
            });
        }
    }
    guardar_lineas_datos_cabecera() {
        this.contenido_tabla.forEach((element) => {
            this.userservice.add_linea_cabecera_datos(element).subscribe((resp) => {
                console.log('ess esto', resp);
            });
        });
        if (this.lineas_valores_cargados == true) {
            this.userservice.add_linea_cabecera_datos(this.contenido_tabla).subscribe((resp) => {
                console.log('ess esto', resp);
                // Swal.fire({
                //   icon: 'success',
                //   title: 'Datos del contenido de la tabla guardados',
                //   showConfirmButton: false,
                //   timer: 3000,
                // }) 
            });
        }
        else {
            Swal.fire({
                icon: 'error',
                title: 'No hay nada que guardar',
                showConfirmButton: false,
                timer: 3000,
            });
        }
    }
    enviardata_imagen_subida() {
        if (this.file) {
            this.loading = !this.loading;
